$(document).ready(function(){$("select.attributeType").each(function(){var a=$(this).find("option:selected").html();if(["Select Box","Radio Group","Check Box"].indexOf(a)>-1){$(this).parents("form").find(".attributeOptions").show()}});$("#showSort").click(function(){$("#attributeList").sortable().disableSelection();$("#showSort").hide();$("#saveSort").show();$(".handle").each(function(a){$(this).show()});return false});$("#saveSort").click(function(){var c=new Array();$("#attributeList > li").each(function(d){c.push($(this).attr("id"))});var a="index.cfm";var b="slatAction=admin:attribute.saveattributesort&attributeID="+c.toString()+"&cacheID="+Math.random();$.post(a+"?"+b);showSort()});$("a.addOption").click(function(){var a=$(this).attr("attribID");var c=$("tr."+a).length;c++;var b=$("#tableTemplate tbody>tr:last").clone(true);b.children("td").children("input").each(function(d){var e=$(this);e.attr("name","options["+c+"]."+e.attr("name"))});$("a[class=remOption][attribID="+a+"]").attr("style","");$("#attrib"+a+" > tbody:last").append(b);b.attr("class",a).attr("id","new"+c+a);return false});$("a.remOption").click(function(){var a=$(this).attr("attribID");var b=$("tr."+a).length;$("#attrib"+a+" tbody>tr:last").remove();if($('tr[id^="new"]').length==0){$("a.remOption").attr("style","display:none;")}return false});$("select.attributeType").change(function(){var a=$(this).find("option:selected").html();if(["Select Box","Radio Group","Check Box"].indexOf(a)>-1){$(this).parents("form").find(".attributeOptions").show()}else{$(this).parents("form").find(".attributeOptions").hide()}})});function btnConfirmAttributeOptionDelete(c,b){var a=$(b).attr("id");$("#alertDialogMessage").html(c);$("#alertDialog").dialog({resizable:false,modal:true,buttons:{YES:function(){$(this).dialog("close");deleteAttributeOption(a)},NO:function(){$(this).dialog("close")}}});return false}function deleteAttributeOption(b){var a={attributeOptionID:b,asynch:"true",cacheID:Math.random()};$.post("index.cfm?slatAction=admin:attribute.deleteAttributeOption",a,function(c){if(c.SUCCESS){$("tr#"+b).fadeOut("normal",function(){$(this).remove()})}else{$("#message"+b).html(c.MESSAGE).show("fast")}},"json")}function showSort(a){$("#showSort").show();$("#saveSort").hide();$(".handle").each(function(b){$(this).hide()});$("#attributeList").sortable("destroy").enableSelection()};